
/**
 @class      Logout
 @brief      Tests the logout functionality
 @details
 @authors    Mike Wong mikewong@sfsu.edu, Jack Cole jcole2@mail.sfsu.edu
 @ingroup    tests
 */
let Test = require( './Test' );

class Logout extends Test {
	get name()     { return "Logout"; }

	execute() {
		return new Promise( async ( resolve, reject ) => {
			try {
				await this.logout();

				resolve( this.result( true, 'Successfully logged out' ));

			} catch( e ) {
				reject( e );
			}
		});
	}
}

module.exports = Logout;
