/**
 *@class    Registration Test
 *@brief    Exercise user registration functionality
 *@details
 *@authors  Mike Wong mikewong@mail.sfsu.edu, Nayana Laxmeshwar nlaxmeshwar@mail.sfsu.edu
 *@ingroup  Registration mixin
**/

const Test           = require( '../tests/Test' );
const UiRegistration = require( '../ui-automation/UiRegistration' );
const mixwith        = require( 'mixwith' );
const mix            = mixwith.mix;

class Registration extends mix( Test ).with( UiRegistration ) {
	get name() { return "Registration"; }

	execute() {
		return new Promise( async ( resolve, reject ) => {
			try {
				await this.register();

				resolve( this.result( true, 'Successfully registered' ));

			} catch( e ) {
				reject( e );
			}
		});
	}
}

module.exports = Registration;
